mapper.select:
select 
gid,gname, gcount, gprice, gweight, gvolume, gtype, gremark,remark5
from goods

	
		gid=#{gid}
	
	
		and gname=%${gname}%
	
	
		and gcount=#{gcount}
	
	
		and gprice between ${minsprice} and ${maxsprice}
	
	
		and gweight between ${minsprice} and ${maxsprice}
	
	
		and gvolume between ${mingvolume} and ${maxgvolume}
	
	
		and gtype=#{gtype}

select 
gid,gname, gcount, gprice, gweight, gvolume, gtype, gremark
from goods

	
		gid=#{gid}
	
	
		and gname=%${gname}%
	
	
		and gcount=#{gcount}
	
	
		and gprice between ${minsprice} and ${maxsprice}
	
	
		and gweight between ${minsprice} and ${maxsprice}
	
	
		and gvolume between ${mingvolume} and ${maxgvolume}
	
	
		and gtype=#{gtype}
